import React, { useEffect } from 'react';

function App() {
  useEffect(() => {
    const script = document.createElement('script');
    script.src = "//webapi.amap.com/maps?v=1.3&key=e07ffdf58c8e8672037bef0d6cae7d4a";
    script.async = true;
    document.body.appendChild(script);

    script.onload = () => {
      if (window.AMap) {
        const AMap = window.AMap;
        var infoWindow, map, level = 11,
          center = { lng: 116.001096, lat: 40.156856 },
          features = [];

        map = new AMap.Map('container', {
          center: new AMap.LngLat(center.lng, center.lat),
          zoom: level
        });

        function loadFeatures() {
          for (var feature, data, i = 0, len = features.length, j, jl, path; i < len; i++) {
            data = features[i];
            switch (data.type) {
              case "Marker":
                feature = new AMap.Marker({
                  map: map,
                  position: new AMap.LngLat(data.lnglat.lng, data.lnglat.lat),
                  zIndex: 3,
                  extData: data,
                  offset: new AMap.Pixel(data.offset.x, data.offset.y),
                  title: data.name,
                  content: '<div class="icon icon-' + data.icon + ' icon-' + data.icon + '-' + data.color + '"></div>'
                });
                break;
              // 其他类型的feature处理
            }
          }
        }

        // 加载features
        loadFeatures();
      } else {
        console.error('AMap is not loaded');
      }
    };

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div id="container" style={{ width: '100%', height: '100vh' }}></div>
  );
}

export default App;
