import React, { useEffect } from 'react';

function App() {
  useEffect(() => {
    var map, district, polygons = [];
    var citySelect = document.getElementById('city');
    var districtSelect = document.getElementById('district');
    var areaSelect = document.getElementById('street');



    map = new AMap.Map('container', {
      center: [116.30946, 39.937629],
      zoom: 3
    });

    var opts = {
      subdistrict: 1,
      showbiz: false
    };
    district = new AMap.DistrictSearch(opts);
    district.search('中国', function (status, result) {
      if (status === 'complete') {
        getData(result.districtList[0]);
      }
    });

    function getData(data, level) {
      var bounds = data.boundaries;
      if (bounds) {
        for (var i = 0, l = bounds.length; i < l; i++) {
          var polygon = new AMap.Polygon({
            map: map,
            strokeWeight: 1,
            strokeColor: '#0091ea',
            fillColor: '#80d8ff',
            fillOpacity: 0.2,
            path: bounds[i]
          });
          polygons.push(polygon);
        }
        map.setFitView();
      }

      if (level === 'province') {
        citySelect.innerHTML = '';
        districtSelect.innerHTML = '';
        areaSelect.innerHTML = '';
      } else if (level === 'city') {
        districtSelect.innerHTML = '';
        areaSelect.innerHTML = '';
      } else if (level === 'district') {
        areaSelect.innerHTML = '';
      }

      var subList = data.districtList;
      if (subList) {
        var contentSub = new Option('--请选择--');
        var curlevel = subList[0].level;
        var curList = document.querySelector('#' + curlevel);
        curList.add(contentSub);
        for (var i = 0, l = subList.length; i < l; i++) {
          var name = subList[i].name;
          var levelSub = subList[i].level;
          contentSub = new Option(name);
          contentSub.setAttribute("value", levelSub);
          contentSub.center = subList[i].center;
          contentSub.adcode = subList[i].adcode;
          curList.add(contentSub);
        }
      }
    }

    window.search = function (obj) {
      for (var i = 0, l = polygons.length; i < l; i++) {
        polygons[i].setMap(null);
      }
      var option = obj[obj.options.selectedIndex];
      var adcode = option.adcode;
      district.setLevel(option.value);
      district.setExtensions('all');
      district.search(adcode, function (status, result) {
        if (status === 'complete') {
          getData(result.districtList[0], obj.id);
        }
      });
    }

    window.setCenter = function (obj) {
      map.setCenter(obj[obj.options.selectedIndex].center);
    }
  }, []);

  return (
    <div>
      <div id="container" style={{ width: '100%', height: '100vh' }}></div>
      <div className="input-card">
        <h4>下属行政区查询</h4>
        <div className="input-item">
          <div className="input-item-prepend"><span className="input-item-text">省市区</span></div>
          <select id='province' style={{ width: '100px' }} onChange={() => window.search(this)}></select>
        </div>
        <div className="input-item">
          <div className="input-item-prepend"><span className="input-item-text">地级市</span></div>
          <select id='city' style={{ width: '100px' }} onChange={() => window.search(this)}></select>
        </div>
        <div className="input-item">
          <div className="input-item-prepend"><span className="input-item-text">区县</span></div>
          <select id='district' style={{ width: '100px' }} onChange={() => window.search(this)}></select>
        </div>
        <div className="input-item">
          <div className="input-item-prepend"><span className="input-item-text">街道</span></div>
          <select id='street' style={{ width: '100px' }} onChange={() => window.setCenter(this)}></select>
        </div>
      </div>
    </div>
  );
}

export default App;
